INSERT INTO categories (id, name, slug, description, meta_description) VALUES
('c1000000-0000-0000-0000-000000000009', 'AI Presentation', 'ai-presentation', 'AI-powered presentation tools that help you create stunning slides, decks, and visual stories automatically using artificial intelligence.', 'Best AI presentation tools of 2026. Create beautiful slides and decks automatically with AI. Compare features, pricing, and reviews.')
